mapper.insert:
  INSERT INTO 
  	TB_FAVORITE 
  		(
			  USER_NO
			, CONTENT_ID
			, CATEGORY
		)
  VALUES 
  		(
			  #{userNo}
			, #{contentId}
			, #{category}
		)

mapper.delete:
DELETE FROM 
	TB_FAVORITE
WHERE 1=1
	AND USER_NO = #{userNo}
  AND CONTENT_ID = #{contentId}
  AND CATEGORY = #{category}

mapper.select:
SELECT 
	  USER_NO AS userNo
	, CONTENT_ID AS contentId
	, CATEGORY AS category
FROM 
	TB_FAVORITE
WHERE 1=1 
	AND USER_NO = #{userNo}
	AND CONTENT_ID = #{contentId}
	AND CATEGORY = #{category}
