mapper.insert:

  SELECT  SEQ_CONTENT_ID.NEXTVAL
    FROM  DUAL

INSERT 
  INTO  TB_CONTENT
    (
        CONTENT_ID
      , CATEGORY_CODE
      , TITLE
      , FIRST_IMAGE
      , TEL
      , HOMEPAGE
      , PLAY_TIME
    )
VALUES
    (
        SEQ_CONTENT_ID.NEXTVAL
      , #{categoryCode}
      , #{title}
      , #{firstImage}
      , #{tel}
      , #{homepage}
      , #{playTime}
    )

INSERT 
  INTO  TB_CONTENT_IMG
    (
        CONTENT_IMG_NO
      , CONTENT_ID
      , FILE_URL
    )
VALUES
    (
        SEQ_CONTENT_IMG_NO.NEXTVAL
      , #{contentId}
      , #{fileUrl}
    )

mapper.update:
UPDATE TB_CONTENT
CATEGORY_CODE = #{categoryCode}, TITLE = #{title}, FIRST_IMAGE = #{firstImage}, TEL = #{tel}, HOMEPAGE = #{homepage}, PLAY_TIME = #{playTime}, MODIFIED_TIME = SYSDATE
WHERE CONTENT_ID = #{contentId}

UPDATE  TB_CONTENT_IMG
   SET  FILE_URL = #{fileUrl}
 WHERE  CONTENT_ID = #{contentId}

UPDATE  TB_CONTENT
   SET  STATUS = 'N'
 WHERE  CONTENT_ID = #{contentId}
