mapper.insert:
INSERT 
  INTO  TB_DETAIL_ADD
    (
        DETAIL_NO
      , CONTENT_ID
      , DONG_NO
      , DETAIL_NAME
      , POST_ADDRESS
    )
VALUES
    (
        SEQ_DETAIL_NO.NEXTVAL
      , #{contentId}
      , #{dongNo}
      , #{detailName}
      , #{postAddress}
    )

mapper.update:
UPDATE  TB_DETAIL_ADD

  DONG_NO = #{dongNo},
  DETAIL_NAME = #{detailName},
  POST_ADDRESS = #{postAddress}

 WHERE  CONTENT_ID = #{contentId}
