mapper.insert:
INSERT 
  INTO  TB_LODGING
    (
        CONTENT_ID
      , LODGING_EXP
      , CHECK_IN
      , CHECK_OUT
      , PARKING
      , ELEVATOR
    )
VALUES
    (
        #{contentId}
      , #{lodgingExp}
      , #{checkIn}
      , #{checkOut}
      , #{parking}
      , #{elevator}
    )

mapper.update:
UPDATE TB_LODGING
LODGING_EXP = #{lodgingExp}, CHECK_IN = #{checkIn}, CHECK_OUT = #{checkOut}, PARKING = #{parking}, ELEVATOR = #{elevator},
WHERE CONTENT_ID = #{contentId}
