mapper.insert:
INSERT 
  INTO  TB_MAP
    (
        CONTENT_ID
      , MAP_X
      , MAP_Y
    )
VALUES
    (
        #{contentId}
      , #{mapX}
      , #{mapY}
    )

INSERT 
  INTO  TB_SIDO
    (
        SIDO_NO
      , SIDO_NAME
    )
VALUES
    (
        #{sidoNo}
      , #{sidoName}
    )

INSERT 
  INTO  TB_SIGUNGU
    (
        SIGUNGU_NO
      , SIDO_NO
      , SIGUNGU_NAME
    )
VALUES
    (
        #{sigunguNo}
      , #{sidoNo}
      , #{sigunguName}
    )

INSERT 
  INTO  TB_DONG
    (
        DONG_NO
      , SIGUNGU_NO
      , DONG_NAME
    )
VALUES
    (
        #{dongNo}
      , #{sigunguNo}
      , #{dongName}
    )

INSERT 
  INTO  TB_DETAIL_ADD
    (
        DETAIL_NO
      , CONTENT_ID
      , DONG_NO
      , DETAIL_NAME
      , POST_ADDRESS
    )
VALUES
    (
        SEQ_DETAIL_NO.NEXTVAL
      , #{contentId}
      , #{dongNo}
      , #{detailName}
      , #{postAddress}
    )
