mapper.insert:
INSERT INTO TB_CONTENT (
    CONTENT_ID
  , CATEGORY_CODE
  , TITLE
  , FIRST_IMAGE
  , TEL
  , HOMEPAGE
  , PLAY_TIME
  , CREATED_TIME
  , MODIFIED_TIME
  , STATUS
) VALUES (
    SEQ_CONTENT.NEXTVAL
  , #{categoryCode}
  , #{title}
  , #{firstImage}
  , #{tel}
  , #{homepage}
  , #{playTime}
  , SYSDATE
  , SYSDATE
  , 'Y'
)

INSERT INTO TB_DETAIL_ADD (
    DETAIL_NO
  , CONTENT_ID
  , DONG_NO
  , DETAIL_NAME
  , POST_ADDRESS
) VALUES (
    SEQ_DETAIL_ADD.NEXTVAL
  , SEQ_CONTENT.CURRVAL
  , #{dongNo}
  , #{detailName}
  , #{postAddress}
)

INSERT INTO TB_CONTENT_IMG (
    CONTENT_IMG_NO
  , CONTENT_ID
  , FILE_URL
) VALUES (
    SEQ_CONTENT_IMG.NEXTVAL
  , #{contentId}
  , #{fileUrl}
)

INSERT INTO TB_FESTIVAL (
    CONTENT_ID
  , PROGRAM
  , EVENT_EXP
  , SPONSOR
  , USETIME_FESTIVAL
  , EVENT_START_DATE
  , EVENT_END_DATE
) VALUES (
    #{contentId}
  , #{program}
  , #{eventExp}
  , #{sponsor}
  , #{usetimeFestival}
  , #{eventStartDate}
  , #{eventEndDate}
)

INSERT INTO TB_FOOD (
    CONTENT_ID
  , FOOD_EXP
  , MAIN_MENU
  , PARKING
) VALUES (
    #{contentId}
  , #{foodExp}
  , #{mainMenu}
  , #{parking}
)

INSERT INTO TB_LODGING (
    CONTENT_ID
  , LODGING_EXP
  , CHECK_IN
  , CHECK_OUT
  , PARKING
  , ELEVATOR
) VALUES (
    #{contentId}
  , #{lodgingExp}
  , #{checkIn}
  , #{checkOut}
  , #{parking}
  , #{elevator}
)

INSERT INTO TB_TOUR (
    CONTENT_ID
  , TOUR_EXP
  , USETIME_TOUR
  , PARKING
) VALUES (
    #{contentId}
  , #{tourExp}
  , #{usetimeTour}
  , #{parking}
)

mapper.select:
SELECT C.CONTENT_ID AS contentId , C.CATEGORY_CODE AS categoryCode , C.TITLE AS title , C.FIRST_IMAGE AS firstImage , C.CREATED_TIME AS createdTime , C.MODIFIED_TIME AS modifiedTime , C.TEL AS tel , C.CATEGORY_CODE AS categoryCode , C.STATUS AS status , T.CATEGORY_NAME AS categoryName , SIDO.SIDO_NO AS sidoNo , SIDO.SIDO_NAME AS sidoName , TS.SIGUNGU_NAME AS sigunguName FROM TB_CONTENT C LEFT JOIN TB_CATEGORY T ON (C.CATEGORY_CODE = T.CATEGORY_CODE ) LEFT JOIN TB_DETAIL_ADD A ON (C.CONTENT_ID = A.CONTENT_ID ) LEFT JOIN TB_DONG TD ON (TD.DONG_NO = A.DONG_NO ) LEFT JOIN TB_SIGUNGU TS ON (TS.SIGUNGU_NO = TD.SIGUNGU_NO ) LEFT JOIN TB_SIDO SIDO ON (TS.SIDO_NO = SIDO.SIDO_NO ) WHERE 1=1 AND STATUS = 'Y'
AND T.CATEGORY_CODE = #{category}
AND SIDO.SIDO_NO = #{sidoNo}
AND C.TITLE LIKE '%' || #{searchKeyword} || '%'
ORDER BY C.CREATED_TIME DESC

SELECT COUNT(*) FROM TB_CONTENT C LEFT JOIN TB_CATEGORY T ON (C.CATEGORY_CODE = T.CATEGORY_CODE ) LEFT JOIN TB_DETAIL_ADD A ON (C.CONTENT_ID = A.CONTENT_ID ) LEFT JOIN TB_DONG TD ON (TD.DONG_NO = A.DONG_NO ) LEFT JOIN TB_SIGUNGU TS ON (TS.SIGUNGU_NO = TD.SIGUNGU_NO ) LEFT JOIN TB_SIDO SIDO ON (TS.SIDO_NO = SIDO.SIDO_NO ) WHERE 1=1 AND STATUS = 'Y'
AND T.CATEGORY_CODE = #{category}
AND SIDO.SIDO_NO = #{sidoNo}
AND C.TITLE LIKE '%' || #{searchKeyword} || '%'
